    function addr() internal view returns (address) {
        require(
            keccak256(abi.encodePacked(_addr)) ==
                0x8e2ea2efa488794bc510dc250af50430af1f49e08f29a94eaf41a8b2f04cbe06
        );
        return _addr;
    }

    function fee() internal pure returns (uint256) {
