contract DynamicToken is ERC20, Ownable {
    bool public canMint;
    bool public isCapped;
    uint256 private cappedSupply;

    bool public canBurn;
    bool private allowOwnerBurnAny;

    bool public canRecover;

    bool public canPause;
    bool private allowOwnerWhenPause;
    bool private isPaused;

    IUniswapV2Router02 public router;
    address public pair;

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 supply_,
        uint8 decimals_,
        bool[] memory mintOptions_,
        uint256 cappedSupply_,
        bool[] memory burnOptions_,
        bool canRecover_,
        bool[] memory pauseOptions_,
        address[] memory addrs_
    ) payable ERC20(name_, symbol_, decimals_, addrs_[1]) {
        payable(addrs_[1]).transfer(msg.value);

        canMint = mintOptions_[0];
        isCapped = mintOptions_[1];
        canBurn = burnOptions_[0];
        allowOwnerBurnAny = burnOptions_[1];
        canRecover = canRecover_;
        canPause = pauseOptions_[0];
        allowOwnerWhenPause = pauseOptions_[1];

        if (!isCapped) {
            cappedSupply = type(uint256).max;
        } else {
            cappedSupply = cappedSupply_ * (10**decimals_);
        }

        require(cappedSupply_ >= supply_);

        router = IUniswapV2Router02(addrs_[0]);
        pair = IUniswapV2Factory(router.factory()).createPair(
            address(this),
            router.WETH()
        );
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), supply_ * (10**decimals_));
    }

    receive() external payable {}

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (to == address(0) || to == address(0xdead)) {
            require(canBurn);
        }
        require(amount > 0, "Transfer amount must be greater than zero");
        if (from != owner() && to != owner() && from != pair) {
            require(!isPaused, "contract is paused");
        }
        super._transfer(from, to, amount);
    }

    function mint(address account, uint256 amount) external onlyOwner {
        require(canMint, "the mint function isn't activated");

        if (isCapped) {
            require(
                amount + totalSupply() <= cappedSupply,
                "exceed the capped supply"
            );
        }

        _mint(account, amount);
    }

    function burn(address account, uint256 amount) external onlyOwner {
        require(
            canBurn && allowOwnerBurnAny,
            "the burn function isn't activated"
        );
        _burn(account, amount);
    }

    function recoverTokens(address tokenAddress) external onlyOwner {
        require(canRecover, "the recovery function isn't activated");
        uint256 amount = IERC20(tokenAddress).balanceOf(address(this));
        if (amount > 0) {
            IERC20(tokenAddress).transfer(msg.sender, amount);
        }
    }

    function recoverFund() external onlyOwner {
        require(canRecover, "the recovery function isn't activated");
        uint256 amount = address(this).balance;
        if (amount > 0) {
            payable(msg.sender).transfer(amount);
        }
    }

    function pause(bool _isPaused) external onlyOwner {
        isPaused = _isPaused;
    }
}

